concept.code:

/* The syntax of pseudo-classes */
selector:pseudo-class {
  property: value;
}

/* unvisited link */
a:link {
  color: #FF0000;
}

/* visited link */
a:visited {
  color: #00FF00;
}

/* mouse over link */
a:hover {
  color: #FF00FF;
}

/* selected link */
a:active {
  color: #0000FF;
}

a.highlight:hover {
  color: #ff0000;
}

div:hover {
  background-color: blue;
}

/* Hover over a <div> element to show a <p> element (like a tooltip) */
p {
  display: none;
  background-color: yellow;
  padding: 20px;
}

div:hover p {
  display: block;
}

/* The :first-child pseudo-class matches a specified element that is the first child of another element. */
p:first-child {
  color: blue;
}
